import { Link } from "react-router-dom";
import React, { Component } from "react";
import { TextField, Button, Snackbar } from "@material-ui/core";

class SignIn extends Component {
  constructor() {
    super();
    this.state = {
      email: "mon@email.com",
      password: "monPassw0rd",
      flash: "",
      open: false
    };
    this.updateEmailField = this.updateEmailField.bind(this);
    this.updatePassword = this.updatePassword.bind(this);
    this.updatePasswordbis = this.updatePasswordbis.bind(this);
    this.updateName = this.updateName.bind(this);
    this.updateLastname = this.updateLastname.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  updateEmailField(event) {
    this.setState({ email: event.target.value });
  }

  updatePassword(event) {
    this.setState({ password: event.target.value });
  }

  updatePasswordbis(event) {
    this.setState({ passwordbis: event.target.value });
  }

  updateName(event) {
    this.setState({ name: event.target.value });
  }

  updateLastname(event) {
    this.setState({ lastname: event.target.value });
  }

  handleClick = () => {
    this.setState({
      open: true
    });
  };

  handleRequestClose = () => {
    this.setState({
      open: false
    });
  };

  handleSubmit(event) {
    console.log("A name was submitted: " + JSON.stringify(this.state, 1, 1));
    fetch("/auth/signin", {
      method: "POST",
      headers: new Headers({
        "Content-Type": "application/json"
      }),
      body: JSON.stringify({
        email: this.state.email,
        password: this.state.password,
        name: this.state.name,
        lastname: this.state.lastname
      })
    })
      .then(res => res.json())
      .then(
        res => this.setState({ flash: res.flash, open: true }),
        err => this.setState({ flash: err.flash, open: true })
      );
    event.preventDefault();
  }
  render() {
    return (
      <div>
        <form onSubmit={this.handleSubmit}>

          <h4>E-mail</h4>
          <TextField
            onChange={this.updateEmailField}
            type="email"
            name="email"
          />
          <h4>Password</h4>
          <TextField
            onChange={this.updatePassword}
            type="password"
            name="password"
          />
          <br />
          <br />
          <Link to="/profile">
            <Button
              color="primary"
              variant="contained"
              type="submit"
              value="Soumettre"
              onClick={this.handleClick}
            >
              Valider
            </Button>
          </Link>
          <br />
          <br />
          <Link to="/signUp">
            Si vous n'avez pas de compte, inscrivez vous ici !!
          </Link>
          <Snackbar
            open={this.state.open}
            message="Vous êtes identifié !!"
            autoHideDuration={4000}
            onRequestClose={this.handleRequestClose}
          />
        </form>
      </div>
    );
  }
}

export default SignIn;
