import React, { Component } from "react";
import {
  TextField,
  Button,
  Snackbar,
  IconButton,
  CloseIcon
} from "@material-ui/core";
import { Link } from "react-router-dom";

class SignUp extends Component {
  constructor() {
    super();
    this.state = {
      email: "mon@email.com",
      password: "monPassw0rd",
      passwordbis: "monPassw0rd",
      name: "James",
      lastname: "Bond",
      open: false
    };
    this.updateEmailField = this.updateEmailField.bind(this);
    this.updatePassword = this.updatePassword.bind(this);
    this.updatePasswordBisField = this.updatePasswordBisField.bind(this);
    this.updateName = this.updateName.bind(this);
    this.updateLastname = this.updateLastname.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleClose = this.handleClose.bind(this);
  }

  updateEmailField(event) {
    this.setState({ email: event.target.value });
  }

  updatePassword(event) {
    this.setState({ password: event.target.value });
  }

  updatePasswordBisField(event) {
    this.setState({ passwordBis: event.target.value });
  }

  updateName(event) {
    this.setState({ name: event.target.value });
  }

  updateLastname(event) {
    this.setState({ lastname: event.target.value });
  }

  handleClose = (event, reason) => {
    if (reason === "clickaway") {
      return;
    }
    this.setState({ open: false });
  };

  handleSubmit(event) {
    console.log(
      `A name has been submitted :${JSON.stringify(this.state, 1, 1)}`
    );
    fetch("/auth/signup", {
      method: "POST",
      headers: new Headers({
        "Content-Type": "application/json"
      }),
      body: JSON.stringify({
        name: this.state.name,
        lastname: this.state.lastname,
        password: this.state.password,
        email: this.state.email
      })
    })
      .then(res => res.json())
      .then(
        res => {
          this.setState({ flash: res.flash, open: true });
        },
        err => {
          this.setState({ flash: err.flash, open: true });
        }
      );
    event.preventDefault();
  }

  render() {
    return (
        <form onSubmit={this.handleSubmit}>
          <h4>E-mail</h4>
          <TextField
            onChange={this.updateEmailField}
            type="email"
            name="email"
          />
          <h4>Password</h4>
          <TextField
            onChange={this.updatePassword}
            type="password"
            name="password"
          />

          <TextField
            type="password"
            name="passwordBis"
            label="Confirm password"
            onChange={this.updatePasswordBisField}
            fullWidth
          />

          <h4>Name</h4>
          <TextField onChange={this.updateName} type="text" name="name" />
          <h4>Lastname</h4>
          <TextField
            onChange={this.updateLastname}
            type="text"
            name="lastname"
          />
          <br />
          <br />

          <div style={{ textAlign: "right", margin: "5%" }}>
          <Link to="/signin">
            <Button variant="contained" color="primary">
              Registered already ?
            </Button>
          </Link>

            <Button
              type="submit"
              variant="contained"
              value="Soumettre"
              color="primary"
            >
              VALIDER
            </Button>

        </div>
           <Snackbar
          anchorOrigin={{
            vertical: "bottom",
            horizontal: "center"
          }}
          open={this.state.open}
          autoHideDuration={6000}
          onClose={this.handleClose}
          ContentProps={{
            "aria-describedby": "message-id"
          }}
          message={
            <span id="message-id">
              {this.state.flash === "User has been signed up !"
                ? this.state.flash
                : "An error occurred..."}
            </span>
          }
          action={[
            <Button
              key="undo"
              color="secondary"
              size="small"
              onClick={this.handleClose}
            >
              UNDO
            </Button>,
            <IconButton
              key="close"
              aria-label="Close"
              color="inherit"
              className={classes.close}
              onClick={this.handleClose}
            >
              <CloseIcon />
            </IconButton>
          ]}
        />
        </form>
    );
  }
}

export default SignUp;
