import React, { Component } from "react";

class SignUp extends Component {
  constructor() {
    super();
    this.state = {
      email: "mon@email.com",
      password: "monPassw0rd",
      name: "James",
      lastname: "Bond"
    };

    this.handleSubmit = this.handleSubmit.bind(this);
  }

  onChangeUpdateEmailField(event) {
    this.setState({ auth: { ...this.state.auth, email: event.target.value } });
  }
  onChangeUpdatePasswordField(event) {
    this.setState({
      auth: { ...this.state.auth, password: event.target.value }
    });
  }
  onChangeUpdatenameField(event) {
    this.setState({
      auth: { ...this.state.auth, name: event.target.value }
    });
  }
  onChangeUpdateLastnameField(event) {
    this.setState({
      auth: { ...this.state.auth, lastname: event.target.value }
    });
  }
  handleSubmit(event) {
    fetch("/auth/signup", {
      method: "POST",
      headers: new Headers({
        "Content-Type": "application/json"
      }),
      body: JSON.stringify(this.state)
    })
      .then(res => res.json())
      .then(
        res => this.setState({ flash: res.flash }),
        err => this.setState({ flash: err.flash })
      );
  }

  render() {
    const { updateField, state, handleSubmit } = { ...this.props };
    return (
      <div>
        <h1>{JSON.stringify(state, 1, 1)}</h1>
        <form onSubmit={event => handleSubmit(event)}>
          <p>
            <input
              onChange={event => updateField(event)}
              type="email"
              name="email"
              placeholder="Votre email"
            />
          </p>
          <p>
            <input
              onChange={event => updateField(event)}
              type="password"
              name="password"
              placeholder="Votre mot de passe"
            />
          </p>
          <p>
            <input
              onChange={event => updateField(event)}
              type="text"
              name="name"
              placeholder="Votre Nom"
            />
          </p>
          <p>
            <input
              onChange={event => updateField(event)}
              type="text"
              name="firstname"
              placeholder="Votre prénom"
            />
          </p>
          <input type="submit" value="Soumettre" />
        </form>
      </div>
    );
  }
}

export default SignUp;
