import React, { Component, state } from "react";

class SignUp extends Component {
  constructor() {
    super();
    this.state = {
      auth: {
        email: "mon@email.com",
        password: "monPassw0rd",
        name: "James",
        lastname: "Bond"
      }
    };

    this.handleSubmit = this.handleSubmit.bind(this);
    this.ChangeUpdateEmail=this.ChangeUpdateEmail.bind(this);
    this.ChangeUpdatePassword=this.ChangeUpdatePassword.bind(this);
    this.ChangeUpdateName=this.ChangeUpdateName.bind(this);
    this.ChangeUpdateLastname=this.ChangeUpdateLastname.bind(this);
  }

  ChangeUpdateEmail(event) {
    this.setState({ auth: { ...this.state.auth, email: event.target.value } });
  }

  ChangeUpdatePassword(event) {
    this.setState({ auth: { ...this.state.auth, password: event.target.value } });
  }

  ChangeUpdateName(event) {
    this.setState({ auth: { ...this.state.auth, name: event.target.value } });
  }

  ChangeUpdateLastname(event) {
    this.setState({ auth: { ...this.state.auth, lastname: event.target.value } });
  }

  handleSubmit(event) {
    console.log(
      "A name was submitted: " + JSON.stringify(this.state.auth, 1, 1)
    );

    event.preventDefault();
    fetch("/auth/signup", {
      method: "POST",
      headers: new Headers({
        "Content-Type": "application/json"
      }),
      body: JSON.stringify(this.state.auth)
    })
      .then(res => res.json())
      .then(
        res => this.setState({ flash: res.flash, open: true }),
        err => this.setState({ flash: err.flash, open: true })
      );
}

  render() {
    return (
      <div>
        <form onSubmit={event => handleSubmit(event)}>
          <p>
            <input
              onChange={event => ChangeUpdateEmail(event)}
              type="email"
              name="email"
              placeholder="Votre email"
            />
          </p>
          <p>
            <input
              onChange={event => ChangeUpdatePassword(event)}
              type="password"
              name="password"
              placeholder="Votre mot de passe"
            />
          </p>
          <p>
            <input
              onChange={event => ChangeUpdateName(event)}
              type="text"
              name="name"
              placeholder="Votre Nom"
            />
          </p>
          <p>
            <input
              onChange={event => ChangeUpdateLastname(event)}
              type="text"
              name="firstname"
              placeholder="Votre prÃ©nom"
            />
          </p>
          <input type="submit" value="Soumettre" />
        </form>
      </div>
    );
  }
}

export default SignUp;
