import React, { Component } from "react";
import { TextField, Snackbar, Button, IconButton } from "@material-ui/core";
import { Link } from "react-router-dom";

class SignIn extends Component {
  constructor(props) {
    super(props);
    this.state = {
      open: false,
      email: "Homer@wild.com",
      password: "odyssey"
    };
    this.handleInputChange = this.handleInputChange.bind(this);
  }
  handleInputChange = event => {
    this.setState({ [event.target.name]: event.target.value });
  };
  handleSubmit(event) {
    console.log(
      `An email has been submitted :${JSON.stringify(this.state, 1, 1)}`
    );
    fetch("/auth/signin", {
      method: "POST",
      headers: new Headers({
        "Content-Type": "application/json"
      }),
      body: JSON.stringify({
        password: this.state.password,
        email: this.state.email
      })
    })
      .then(res => res.json())
      .then(
        res => {
          this.setState({ flash: res.flash, open: true });
        },
        err => {
          this.setState({ flash: err.flash, open: true });
        }
      );
    event.preventDefault();
  }

  handleClose = () => {
    this.setState({
      open: false
    });
  };

  render() {
    return (
      <div>
        <form onSubmit={event => this.handleSubmit(event)}>
          <TextField
            label="Email"
            onChange={event => this.handleInputChange(event)}
            type="email"
            name="email"
          />
          <TextField
            label="Password"
            onChange={event => this.handleInputChange(event)}
            type="password"
            name="password"
          />
          <br />
          <br />
          <Button
            color="secondary"
            variant="contained"
            type="submit"
            value="Soumettre"
            onClick={this.handleClick}
          >
            Valider
          </Button>
          <br />
          <br />
          <Link to="/signup">
            Si vous n'avez pas de compte, inscrivez vous ici !!
          </Link>

          <Snackbar
            anchorOrigin={{
              vertical: "bottom",
              horizontal: "center"
            }}
            open={this.state.open}
            autoHideDuration={6000}
            onClose={this.handleClose}
            ContentProps={{
              "aria-describedby": "message-id"
            }}
            message={
              <span id="message-id">
                {this.state.flash === "User has been signed in !"
                  ? this.state.flash
                  : "An error occurred..."}
              </span>
            }
            action={[
              <Button
                key="undo"
                color="secondary"
                size="small"
                onClick={this.handleClose}
              >
                UNDO
              </Button>,
              <IconButton
                key="close"
                aria-label="Close"
                color="inherit"
                className={classes.close}
                onClick={this.handleClose}
              />
            ]}
          />
        </form>
      </div>
    );
  }
}

export default SignIn;
