import React, { Component } from "react";
import { TextField } from "@material-ui/core";

class SignUp extends Component {
  constructor() {
    super();
    this.state = {
      auth: {
        email: "mon@email.com",
        password: "monPassw0rd",
        name: "James",
        lastname: "Bond"
      }
    };

    this.updateEmail = this.updateEmail.bind(this);
    this.updatePassword = this.updatePassword.bind(this);
    this.updateName = this.updateName.bind(this);
    this.updateLastname = this.updateLastname.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  updateEmail(event) {
    this.setState({ email: event.target.value });
  }

  updatePassword(event) {
    this.setState({ password: event.target.value });
  }

  updateName(event) {
    this.setState({ name: event.target.value });
  }

  updateLastname(event) {
    this.setState({ lastname: event.target.value });
  }

  handleSubmit(event) {
    console.log(
      "A name was submitted: " + JSON.stringify(this.state.auth, 1, 1)
    );

    event.preventDefault();
    fetch("/auth/signup", {
      method: "POST",
      headers: new Headers({
        "Content-Type": "application/json"
      }),
      body: JSON.stringify(this.state.auth)
    })
      .then(res => res.json())
      .then(
        res => this.setState({ flash: res.flash, open: true }),
        err => this.setState({ flash: err.flash, open: true })
      );
  }

  render() {
    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          {/* <h1>{JSON.stringify(this.state, 1, 1)}</h1>  */}
          <h4>E-mail</h4>
          <TextField
            onChange={this.updateEmail}
            type="email"
            name="email"
          />
          <h4>Password</h4>
          <TextField
            onChange={this.updatePassword}
            type="password"
            name="password"
          />

          <h4>Name</h4>
          <TextField onChange={this.updateName} type="text" name="name" />
          <h4>Lastname</h4>
          <TextField
            onChange={this.updateLastname}
            type="text"
            name="lastname"
          />

        </form>
</div>
    );
  }
}

export default SignUp;
